class Solution {
    public Object rounded_avg(int n, int m) {
        // Step 1: Check if n is greater than m
        if (n > m) {
            return -1;
        }

        // Step 2: Calculate the sum of all integers from n to m
        long sum = 0;
        for (int i = n; i <= m; i++) {
            sum += i;
        }

        // Step 3: Compute the average
        long count = m - n + 1;
        double average = (double) sum / count;

        // Step 4: Round the average to the nearest integer
        long roundedAverage = Math.round(average);

        // Step 5: Convert the rounded average to binary
        String binaryRepresentation = Long.toBinaryString(roundedAverage);

        return binaryRepresentation;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.rounded_avg(1, 5));  // Output: "11"
        System.out.println(solution.rounded_avg(7, 5));  // Output: -1
        System.out.println(solution.rounded_avg(10, 20)); // Output: "1111"
        System.out.println(solution.rounded_avg(20, 33)); // Output: "11011"
    }
}

