class Solution {
    public List<Integer> unique_digits(List<Integer> x) {
        // Filter out numbers with even digits
        List<Integer> filteredList = new ArrayList<>();
        for (Integer num : x) {
            if (!containsEvenDigit(num)) {
                filteredList.add(num);
            }
        }

        // Sort the filtered list
        Collections.sort(filteredList);

        return filteredList;
    }

    private boolean containsEvenDigit(int num) {
        while (num > 0) {
            int digit = num % 10;
            if (digit % 2 == 0) {
                return true;
            }
            num /= 10;
        }
        return false;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.unique_digits(Arrays.asList(15, 33, 1422, 1))); // [1, 15, 33]
        System.out.println(solution.unique_digits(Arrays.asList(152, 323, 1422, 10))); // []
    }
}

