        "One", "Two", "Three", "Four", "Five", "Six", "Seven", "Eight", "Nine"
    };

    public List<String> by_length(List<Integer> arr) {
        // Step 1: Filter out numbers not between 1 and 9
        List<Integer> filteredList = new ArrayList<>();
        for (int num : arr) {
            if (num >= 1 && num <= 9) {
                filteredList.add(num);
            }
        }

        // Step 2: Sort the filtered list
        Collections.sort(filteredList);

        // Step 3: Reverse the sorted list
        Collections.reverse(filteredList);

        // Step 4: Replace each number with its corresponding word
        List<String> result = new ArrayList<>();
        for (int num : filteredList) {
            result.add(NUMBER_WORDS[num - 1]);
        }

        return result;
    }

