    }
}

import java.util.*;
import java.lang.*;

class Solution {
    /**
    Write a function count_nums which takes an array of integers and returns
    the number of elements which has a sum of digits > 0.
    If a number is negative, then its first signed digit will be negative:
    e.g. -123 has signed digits -1, 2, and 3.
    >>> count_nums(Arrays.asList()) == 0
    >>> count_nums(Arrays.asList(-1, 11, -11)) == 1
    >>> count_nums(Arrays.asList(1, 1, 2)) == 3
     */
    public int count_nums(List<Integer> arr) {
        int count = 0;
        for (int num : arr) {
            if (sumOfDigits(num) > 0) {
                count++;
            }
        }
        return count;
    }

    private int sumOfDigits(int num) {
        boolean isNegative = num < 0;
        int sum = 0;
        while (num != 0) {
            int digit = num % 10;
            sum += digit;
            num /= 10;
        }
        // If the number was negative, the first digit should be negative
        if (isNegative && sum != 0) {
            sum -= 2 * (sum % 10);
        }
        return sum;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.count_nums(Arrays.asList())); // 0
        System.out.println(solution.count_nums(Arrays.asList(-1, 11, -11))); // 1
        System.out.println(solution.count_nums(Arrays.asList(1, 1, 2))); // 3
    }
}

import java.util.*;

class Solution {
    public boolean move_one_ball(List<Integer> arr) {
        // If the array is empty, return true
        if (arr.isEmpty()) {
            return true;
        }

        int n = arr.size();
        int discontinuityPoint = -1;

        // Find the point of discontinuity
        for (int i = 0; i < n - 1; i++) {
            if (arr.get(i) > arr.get(i + 1)) {
                if (discontinuityPoint != -1) {
                    // More than one discontinuity point found
                    return false;
                }
                discontinuityPoint = i;
            }
        }

        // Check if the array is already sorted
        if (discontinuityPoint == -1) {
            return true;
        }

        // Check if the array can be split and reassembled
        // The last element should be less than or equal to the first element
        if (arr.get(n - 1) > arr.get(0)) {
            return false;
        }

        // Check the remaining part of the array
        for (int i = 0; i < discontinuityPoint; i++) {
            if (arr.get(i) > arr.get(i + 1)) {
                return false;
            }
        }

        return true;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.move_one_ball(Arrays.asList(3, 4, 5, 1, 2))); // true
        System.out.println(solution.move_one_ball(Arrays.asList(3, 5, 4, 1, 2))); // false
        System.out.println(solution.move_one_ball(Arrays.asList(1, 2, 3, 4, 5))); // true
        System.out.println(solution.move_one_ball(Arrays.asList())); // true
    }
}

