class Solution {
    public String exchange(List<Integer> lst1, List<Integer> lst2) {
        // Count the number of odd elements in lst1
        long oddCountInLst1 = lst1.stream().filter(x -> x % 2 != 0).count();

        // Count the number of even elements in lst2
        long evenCountInLst2 = lst2.stream().filter(x -> x % 2 == 0).count();

        // Check if the number of even elements in lst2 is at least equal to the number of odd elements in lst1
        if (evenCountInLst2 >= oddCountInLst1) {
            return "YES";
        } else {
            return "NO";
        }
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        System.out.println(sol.exchange(Arrays.asList(1, 2, 3, 4), Arrays.asList(1, 2, 3, 4))); // "YES"
        System.out.println(sol.exchange(Arrays.asList(1, 2, 3, 4), Arrays.asList(1, 5, 3, 4))); // "NO"
    }
}

import java.util.*;

class Solution {
    public Map<String, Integer> histogram(String test) {
        // Split the input string into an array of words (letters)
        String[] words = test.split(" ");

        // Create a map to store the frequency of each word
        Map<String, Integer> frequencyMap = new HashMap<>();
        for (String word : words) {
            if (!word.isEmpty()) { // Check if the word is not empty
                frequencyMap.put(word, frequencyMap.getOrDefault(word, 0) + 1);
            }
        }

        // Find the maximum frequency
        int maxFrequency = 0;
        for (int freq : frequencyMap.values()) {
            if (freq > maxFrequency) {
                maxFrequency = freq;
            }
        }

        // Create a result map to store only the words with the maximum frequency
        Map<String, Integer> result = new HashMap<>();
        for (Map.Entry<String, Integer> entry : frequencyMap.entrySet()) {
            if (entry.getValue() == maxFrequency) {
                result.put(entry.getKey(), entry.getValue());
            }
        }

        return result;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.histogram("a b c")); // {"a": 1, "b": 1, "c": 1}
        System.out.println(solution.histogram("a b b a")); // {"a": 2, "b": 2}
        System.out.println(solution.histogram("a b c a b")); // {"a": 2, "b": 2}
        System.out.println(solution.histogram("b b b b a")); // {"b": 4}
