import java.util.*;

class Solution {
    public List<Object> reverse_delete(String s, String c) {
        // Step 1: Remove all characters from s that are present in c
        StringBuilder result = new StringBuilder();
        for (char ch : s.toCharArray()) {
            if (c.indexOf(ch) == -1) {
                result.append(ch);
            }
        }

        // Step 2: Check if the resulting string is a palindrome
        String resultStr = result.toString();
        String reversedStr = result.reverse().toString();
        boolean isPalindrome = resultStr.equals(reversedStr);

        // Step 3: Return the result as a list
        return Arrays.asList(resultStr, isPalindrome);
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.reverse_delete("abcde", "ae")); // Output: [bcd, false]
        System.out.println(solution.reverse_delete("abcdef", "b"));  // Output: [acdef, false]
        System.out.println(solution.reverse_delete("abcdedcba", "ab")); // Output: [cdedc, true]
    }
}

