import java.util.*;

class Solution {
    public int minSubArraySum(List<Integer> nums) {
        if (nums == null || nums.isEmpty()) {
            throw new IllegalArgumentException("Input list cannot be null or empty");
        }

        int minSum = nums.get(0);
        int currentSum = nums.get(0);

        for (int i = 1; i < nums.size(); i++) {
            // If currentSum is positive, starting fresh from the current element might give a smaller sum
            currentSum = Math.min(nums.get(i), currentSum + nums.get(i));
            minSum = Math.min(minSum, currentSum);
        }

        return minSum;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.minSubArraySum(Arrays.asList(2, 3, 4, 1, 2, 4))); // Output: 1
        System.out.println(solution.minSubArraySum(Arrays.asList(-1, -2, -3))); // Output: -6
    }
}

