class Solution {
    public int max_fill(List<List<Integer>> grid, int capacity) {
        int totalWater = 0;

        // Count the total number of units of water in all wells
        for (List<Integer> row : grid) {
            for (int cell : row) {
                totalWater += cell;
            }
        }

        // Calculate the number of times we need to lower the buckets
        return (int) Math.ceil((double) totalWater / capacity);
    }

