class Solution {
    public List<Integer> sort_array(List<Integer> arr) {
        Collections.sort(arr, new Comparator<Integer>() {
            @Override
            public int compare(Integer o1, Integer o2) {
                // Count the number of ones in the binary representation of both numbers
                int count1 = Integer.bitCount(o1);
                int count2 = Integer.bitCount(o2);

                // If the counts are different, sort by the count of ones
                if (count1 != count2) {
                    return count1 - count2;
                }

                // If the counts are the same, sort by the decimal value
                return o1 - o2;
            }
        });

        return arr;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.sort_array(Arrays.asList(1, 5, 2, 3, 4))); // [1, 2, 4, 3, 5]
        System.out.println(solution.sort_array(Arrays.asList(-2, -3, -4, -5, -6))); // [-4, -2, -6, -5, -3]
        System.out.println(solution.sort_array(Arrays.asList(1, 0, 2, 3, 4))); // [0, 1, 2, 4, 3]
    }
}

