class Solution {
    public List<String> select_words(String s, int n) {
        // Split the string into words
        String[] words = s.split("\\s+");

        // Define a set of vowels for easy checking
        Set<Character> vowels = new HashSet<>(Arrays.asList('a', 'e', 'i', 'o', 'u'));

        // Create a list to store the result
        List<String> result = new ArrayList<>();

        // Iterate over each word
        for (String word : words) {
            // Count the number of consonants in the word
            int consonantCount = 0;
            for (char c : word.toLowerCase().toCharArray()) {
                if (!vowels.contains(c)) {
                    consonantCount++;
                }
            }

            // If the word has exactly n consonants, add it to the result list
            if (consonantCount == n) {
                result.add(word);
            }
        }

        // Return the result list
        return result;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.select_words("Mary had a little lamb", 4)); // ["little"]
        System.out.println(solution.select_words("Mary had a little lamb", 3)); // ["Mary", "lamb"]
        System.out.println(solution.select_words("simple white space", 2));     // []
        System.out.println(solution.select_words("Hello world", 4));            // ["world"]
        System.out.println(solution.select_words("Uncle sam", 3));              // ["Uncle"]
    }
}

import java.util.*;

class Solution {
    public String get_closest_vowel(String word) {
        // Define the set of vowels
        Set<Character> vowels = new HashSet<>(Arrays.asList('a', 'e', 'i', 'o', 'u', 'A', 'E', 'I', 'O', 'U'));

        // Iterate from the right to the left
        for (int i = word.length() - 1; i > 0; i--) {
            char currentChar = word.charAt(i);
            char prevChar = word.charAt(i - 1);

            // Check if the current character is a vowel and the previous character is a consonant
            if (vowels.contains(currentChar) && !vowels.contains(prevChar)) {
                // Check if the next character (if exists) is a consonant
                if (i + 1 < word.length() && !vowels.contains(word.charAt(i + 1))) {
                    return String.valueOf(currentChar);
                }
            }
        }

        // Return an empty string if no such vowel is found
        return "";
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.get_closest_vowel("yogurt")); // "u"
        System.out.println(solution.get_closest_vowel("FULL"));   // "U"
        System.out.println(solution.get_closest_vowel("quick"));  // ""
        System.out.println(solution.get_closest_vowel("ab"));     // ""
    }
}

import java.util.*;

class Solution {
    public String match_parens(List<String> lst) {
        // Extract the two strings
        String s1 = lst.get(0);
        String s2 = lst.get(1);

        // Count the number of open and close parentheses in each string
        int open1 = 0, close1 = 0;
        for (char c : s1.toCharArray()) {
            if (c == '(') open1++;
            else close1++;
        }

        int open2 = 0, close2 = 0;
        for (char c : s2.toCharArray()) {
            if (c == '(') open2++;
            else close2++;
        }

        // Check if concatenating in either order results in a balanced string
        boolean isValidOrder1 = (open1 >= close2 && open1 + open2 == close1 + close2);
        boolean isValidOrder2 = (open2 >= close1 && open1 + open2 == close1 + close2);

        // Return "Yes" if either order is valid, otherwise "No"
        return (isValidOrder1 || isValidOrder2) ? "Yes" : "No";
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.match_parens(Arrays.asList("()(", ")"))); // Output: "Yes"
        System.out.println(solution.match_parens(Arrays.asList(")", ")")));   // Output: "No"
    }
}

