class Solution {
    public List<Integer> get_odd_collatz(int n) {
        Set<Integer> oddNumbers = new HashSet<>();

        // Generate the Collatz sequence and collect odd numbers
        while (n != 1) {
            if (n % 2 == 1) { // Check if n is odd
                oddNumbers.add(n);
            }
            n = (n % 2 == 0) ? n / 2 : 3 * n + 1; // Apply Collatz rule
        }
        oddNumbers.add(1); // Add 1 to the set as it is always part of the sequence

        // Convert the set to a list and sort it
        List<Integer> result = new ArrayList<>(oddNumbers);
        Collections.sort(result);

        return result;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.get_odd_collatz(5)); // Output: [1, 5]
        System.out.println(solution.get_odd_collatz(7)); // Output: [1, 7, 11, 22]
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    public boolean valid_date(String date) {
        // Check if the date string is empty
        if (date == null || date.isEmpty()) {
            return false;
        }

        // Regular expression to match the date format mm-dd-yyyy
        String regex = "^(0[1-9]|1[0-2])-(0[1-9]|[12][0-9]|3[01])-(\\d{4})$";
        if (!date.matches(regex)) {
            return false;
        }

        // Extract the month, day, and year from the date string
        String[] parts = date.split("-");
        int month = Integer.parseInt(parts[0]);
        int day = Integer.parseInt(parts[1]);
        int year = Integer.parseInt(parts[2]);

        // Validate the month
        if (month < 1 || month > 12) {
            return false;
        }

        // Validate the day based on the month
        if (day < 1) {
            return false;
        }
        if (month == 2) { // February
            if (isLeapYear(year)) {
                if (day > 29) {
                    return false;
                }
            } else {
                if (day > 28) {
                    return false;
                }
            }
        } else if (month == 4 || month == 6 || month == 9 || month == 11) { // April, June, September, November
            if (day > 30) {
                return false;
            }
        } else { // January, March, May, July, August, October, December
            if (day > 31) {
                return false;
            }
        }

        // If all checks pass, the date is valid
        return true;
    }

    // Helper method to check if a year is a leap year
    private boolean isLeapYear(int year) {
        return (year % 4 == 0 && year % 100 != 0) || (year % 400 == 0);
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.valid_date("03-11-2000")); // true
        System.out.println(solution.valid_date("15-01-2012")); // false
        System.out.println(solution.valid_date("04-0-2040"));  // false
        System.out.println(solution.valid_date("06-04-2020")); // true
        System.out.println(solution.valid_date("06/04/2020")); // false
    }
}

