import java.util.*;

class Solution {
    public boolean is_sorted(List<Integer> lst) {
        if (lst == null || lst.size() <= 1) {
            return true;
        }

        Map<Integer, Integer> countMap = new HashMap<>();
        int prev = lst.get(0);
        countMap.put(prev, 1);

        for (int i = 1; i < lst.size(); i++) {
            int current = lst.get(i);

            // Check if the list is sorted
            if (current < prev) {
                return false;
            }

            // Update the count map
            countMap.put(current, countMap.getOrDefault(current, 0) + 1);

            // Check if any number appears more than twice
            if (countMap.get(current) > 2) {
                return false;
            }

            prev = current;
        }

        return true;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.is_sorted(Arrays.asList(5))); // true
        System.out.println(solution.is_sorted(Arrays.asList(1, 2, 3, 4, 5))); // true
        System.out.println(solution.is_sorted(Arrays.asList(1, 3, 2, 4, 5))); // false
        System.out.println(solution.is_sorted(Arrays.asList(1, 2, 3, 4, 5, 6))); // true
        System.out.println(solution.is_sorted(Arrays.asList(1, 2, 3, 4, 5, 6, 7))); // true
        System.out.println(solution.is_sorted(Arrays.asList(1, 3, 2, 4, 5, 6, 7))); // false
        System.out.println(solution.is_sorted(Arrays.asList(1, 2, 2, 3, 3, 4))); // true
        System.out.println(solution.is_sorted(Arrays.asList(1, 2, 2, 2, 3, 4))); // false
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    public String intersection(List<Integer> interval1, List<Integer> interval2) {
        // Extract the start and end points of both intervals
        int start1 = interval1.get(0);
        int end1 = interval1.get(1);
        int start2 = interval2.get(0);
        int end2 = interval2.get(1);

        // Determine the intersection
        int intersectionStart = Math.max(start1, start2);
        int intersectionEnd = Math.min(end1, end2);

        // If there is no intersection, return "NO"
        if (intersectionStart > intersectionEnd) {
            return "NO";
        }

        // Calculate the length of the intersection
        int intersectionLength = intersectionEnd - intersectionStart + 1;

        // Check if the length is a prime number
        if (isPrime(intersectionLength)) {
            return "YES";
        } else {
            return "NO";
        }
    }

    // Helper method to check if a number is prime
    private boolean isPrime(int n) {
        if (n <= 1) {
            return false;
        }
        if (n <= 3) {
            return true;
        }
        if (n % 2 == 0 || n % 3 == 0) {
            return false;
        }
        for (int i = 5; i * i <= n; i += 6) {
            if (n % i == 0 || n % (i + 2) == 0) {
                return false;
            }
        }
        return true;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.intersection(Arrays.asList(1, 2), Arrays.asList(2, 3))); // "NO"
        System.out.println(solution.intersection(Arrays.asList(-1, 1), Arrays.asList(0, 4))); // "NO"
        System.out.println(solution.intersection(Arrays.asList(-3, -1), Arrays.asList(-5, 5))); // "YES"
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    public Optional<Integer> prod_signs(List<Integer> arr) {
        if (arr.isEmpty()) {
            return Optional.empty();
        }

        int sumOfMagnitudes = 0;
        int productOfSigns = 1;

        for (int num : arr) {
            sumOfMagnitudes += Math.abs(num);
            if (num == 0) {
                productOfSigns = 0;
                break; // If any number is 0, the product of signs will be 0
            } else if (num < 0) {
                productOfSigns *= -1;
            }
        }

        return Optional.of(sumOfMagnitudes * productOfSigns);
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.prod_signs(Arrays.asList(1, 2, 2, -4))); // Output: Optional[-9]
        System.out.println(solution.prod_signs(Arrays.asList(0, 1))); // Output: Optional[0]
        System.out.println(solution.prod_signs(Arrays.asList())); // Output: Optional.empty
    }
}

import java.util.*;

class Solution {
    private static final int[][] DIRECTIONS = {{0, 1}, {1, 0}, {0, -1}, {-1, 0}};

    public List<Integer> minPath(List<List<Integer>> grid, int k) {
        int n = grid.size();
        PriorityQueue<Path> pq = new PriorityQueue<>((a, b) -> {
            for (int i = 0; i < Math.min(a.path.size(), b.path.size()); i++) {
                if (!a.path.get(i).equals(b.path.get(i))) {
                    return a.path.get(i) - b.path.get(i);
                }
            }
            return a.path.size() - b.path.size();
        });

        // Initialize the priority queue with all possible starting points
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                pq.offer(new Path(new ArrayList<>(List.of(grid.get(i).get(j))), i, j));
            }
        }

        while (!pq.isEmpty()) {
            Path current = pq.poll();
            if (current.path.size() == k) {
                return current.path;
            }

            for (int[] dir : DIRECTIONS) {
                int newRow = current.row + dir[0];
                int newCol = current.col + dir[1];

                if (newRow >= 0 && newRow < n && newCol >= 0 && newCol < n) {
                    List<Integer> newPath = new ArrayList<>(current.path);
                    newPath.add(grid.get(newRow).get(newCol));
                    pq.offer(new Path(newPath, newRow, newCol));
                }
            }
        }

        return new ArrayList<>(); // This line should never be reached if input is valid
    }

    private static class Path {
        List<Integer> path;
        int row;
        int col;

        Path(List<Integer> path, int row, int col) {
            this.path = path;
            this.row = row;
            this.col = col;
        }
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        List<List<Integer>> grid1 = Arrays.asList(
            Arrays.asList(1, 2, 3),
            Arrays.asList(4, 5, 6),
            Arrays.asList(7, 8, 9)
        );
        System.out.println(solution.minPath(grid1, 3)); // Output: [1, 2, 1]

        List<List<Integer>> grid2 = Arrays.asList(
            Arrays.asList(5, 9, 3),
            Arrays.asList(4, 1, 6),
            Arrays.asList(7, 8, 2)
        );
        System.out.println(solution.minPath(grid2, 1)); // Output: [1]
    }
}

import java.util.*;

class Solution {
    public List<Integer> tri(int n) {
        List<Integer> result = new ArrayList<>();
        for (int i = 1; i <= n + 1; i++) {
            result.add(triRecursive(i));
        }
        return result;
    }

    private int triRecursive(int n) {
        if (n == 1) {
            return 3;
        } else if (n % 2 == 0) {
            return 1 + n / 2;
        } else {
            return triRecursive(n - 1) + triRecursive(n - 2) + triRecursive(n + 1);
        }
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        int n = 3;
        List<Integer> result = solution.tri(n);
        System.out.println(result); // Expected output: [1, 3, 2, 8]
    }
}

import java.util.*;
