class Solution {
    public int digits(int n) {
        int product = 1;
        boolean hasOdd = false;

        while (n > 0) {
            int digit = n % 10; // Extract the last digit
            if (digit % 2 != 0) { // Check if the digit is odd
                product *= digit; // Multiply the product by the odd digit
                hasOdd = true; // Mark that we have found an odd digit
            }
            n /= 10; // Remove the last digit
        }

        return hasOdd ? product : 0; // Return the product if there are odd digits, otherwise return 0
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        System.out.println(sol.digits(1));    // Output: 1
        System.out.println(sol.digits(4));    // Output: 0
        System.out.println(sol.digits(235));  // Output: 15
        System.out.println(sol.digits(2468)); // Output: 0
    }
}

