class Solution {
    public boolean is_nested(String string) {
        Stack<Character> stack = new Stack<>();
        boolean hasNested = false;

        for (char ch : string.toCharArray()) {
            if (ch == '[') {
                stack.push(ch);
            } else if (ch == ']') {
                if (!stack.isEmpty()) {
                    stack.pop();
                    // Check if there is still an opening bracket in the stack
                    if (!stack.isEmpty()) {
                        hasNested = true;
                    }
                }
            }
        }

        return hasNested;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.is_nested("[[]]")); // true
        System.out.println(solution.is_nested("[]]]]]]][[[[[]")); // false
        System.out.println(solution.is_nested("[][]")); // false
        System.out.println(solution.is_nested("[]")); // false
        System.out.println(solution.is_nested("[[][]]")); // true
        System.out.println(solution.is_nested("[[]][[")); // true
    }
}

