import java.util.*;
import java.lang.*;

class Solution {
    /**
    Create a function that returns true if the last character
    of a given string is an alphabetical character and is not
    a part of a word, and false otherwise.
    Note: "word" is a group of characters separated by space.

    Examples:
    check_if_last_char_is_a_letter("apple pie") -> false
    check_if_last_char_is_a_letter("apple pi e") -> true
    check_if_last_char_is_a_letter("apple pi e ") -> false
    check_if_last_char_is_a_letter("") -> false
     */
    public boolean check_if_last_char_is_a_letter(String txt) {
        // Trim the string to remove trailing spaces
        String trimmedTxt = txt.trim();

        // Check if the trimmed string is empty
        if (trimmedTxt.isEmpty()) {
            return false;
        }

        // Get the last character of the trimmed string
        char lastChar = trimmedTxt.charAt(trimmedTxt.length() - 1);

        // Check if the last character is an alphabetical character
        if (!Character.isLetter(lastChar)) {
            return false;
        }

        // Check if the last character is the only character in the string
        // or if it is followed by a space
        int lastCharIndex = txt.lastIndexOf(lastChar);
        if (lastCharIndex == txt.length() - 1 || txt.charAt(lastCharIndex + 1) == ' ') {
            return true;
        }

        return false;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.check_if_last_char_is_a_letter("apple pie")); // false
        System.out.println(solution.check_if_last_char_is_a_letter("apple pi e")); // true
        System.out.println(solution.check_if_last_char_is_a_letter("apple pi e ")); // false
        System.out.println(solution.check_if_last_char_is_a_letter("")); // false
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    /**
    Create a function which returns the largest index of an element which
    is not greater than or equal to the element immediately preceding it. If
    no such element exists then return -1. The given array will not contain
    duplicate values.

    Examples:
    can_arrange(Arrays.asList(1,2,4,3,5)) = 3
    can_arrange(Arrays.asList(1,2,3)) = -1
     */
    public int can_arrange(List<Integer> arr) {
        // Iterate from the end of the list to the beginning
        for (int i = arr.size() - 1; i > 0; i--) {
            // Check if the current element is less than the previous element
            if (arr.get(i) < arr.get(i - 1)) {
                // Return the index of the current element
                return i;
            }
        }
        // If no such element is found, return -1
        return -1;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.can_arrange(Arrays.asList(1, 2, 4, 3, 5))); // Output: 3
        System.out.println(solution.can_arrange(Arrays.asList(1, 2, 3)));       // Output: -1
    }
}

