import java.util.Optional;

public class Solution {
    public Optional<Object> compare_one(Object a, Object b) {
        double valA = toDouble(a);
        double valB = toDouble(b);

        if (valA == valB) {
            return Optional.empty();
        } else if (valA > valB) {
            return Optional.of(a);
        } else {
            return Optional.of(b);
        }
    }

    private double toDouble(Object obj) {
        if (obj instanceof String) {
            String str = (String) obj;
            str = str.replace(',', '.');
            return Double.parseDouble(str);
        } else if (obj instanceof Integer) {
            return ((Integer) obj).doubleValue();
        } else if (obj instanceof Double) {
            return (Double) obj;
        } else {
            throw new IllegalArgumentException("Unsupported type: " + obj.getClass().getName());
        }
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.compare_one(1, 2.5)); // Optional.of(2.5)
        System.out.println(solution.compare_one(1, "2,3")); // Optional.of("2,3")
        System.out.println(solution.compare_one("5,1", "6")); // Optional.of("6")
        System.out.println(solution.compare_one("1", 1)); // Optional.empty()
    }
}

