class Solution {
    /**
    The Brazilian factorial is defined as:
    brazilian_factorial(n) = n! * (n-1)! * (n-2)! * ... * 1!
    where n > 0

    For example:
    >>> special_factorial(4)
    288

    The function will receive an integer as input and should return the special
    factorial of this integer.
     */
    public long special_factorial(int n) {
        long result = 1;
        for (int i = 1; i <= n; i++) {
            result *= factorial(i);
        }
        return result;
    }

    // Helper method to compute the factorial of a single number
    private long factorial(int num) {
        long fact = 1;
        for (int i = 1; i <= num; i++) {
            fact *= i;
        }
        return fact;
    }

    // Main method for testing
    public static void main(String[] args) {
        Solution sol = new Solution();
        System.out.println(sol.special_factorial(4)); // Expected output: 288
        System.out.println(sol.special_factorial(5)); // Expected output: 34560
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    public String fix_spaces(String text) {
        StringBuilder result = new StringBuilder();
        int spaceCount = 0;

        for (char c : text.toCharArray()) {
            if (c == ' ') {
                spaceCount++;
            } else {
                if (spaceCount > 2) {
                    result.append('-');
                } else if (spaceCount > 0) {
                    result.append(String.valueOf('_').repeat(Math.max(0, spaceCount)));
                }
                spaceCount = 0;
                result.append(c);
            }
        }

        // Handle trailing spaces
        if (spaceCount > 2) {
            result.append('-');
        } else if (spaceCount > 0) {
            result.append(String.valueOf('_').repeat(Math.max(0, spaceCount)));
        }

        return result.toString();
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.fix_spaces("Example")); // "Example"
        System.out.println(solution.fix_spaces("Example 1")); // "Example_1"
        System.out.println(solution.fix_spaces(" Example 2")); // "_Example_2"
        System.out.println(solution.fix_spaces(" Example   3")); // "_Example-3"
    }
}

import java.util.*;

class Solution {
    public String file_name_check(String file_name) {
        // Split the file name into two parts using the dot as a delimiter
        String[] parts = file_name.split("\\.", -1);

        // Check if there is exactly one dot
        if (parts.length != 2) {
            return "No";
        }

        // Extract the name and extension parts
        String namePart = parts[0];
        String extensionPart = parts[1];

        // Check if the name part is not empty and starts with a letter
        if (namePart.isEmpty() || !Character.isLetter(namePart.charAt(0))) {
            return "No";
        }

        // Check if the name part contains more than three digits
        long digitCount = namePart.chars().filter(Character::isDigit).count();
        if (digitCount > 3) {
            return "No";
        }

        // Check if the extension part is one of the allowed extensions
        Set<String> allowedExtensions = new HashSet<>(Arrays.asList("txt", "exe", "dll"));
        if (!allowedExtensions.contains(extensionPart)) {
            return "No";
        }

        // If all checks pass, the file name is valid
        return "Yes";
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.file_name_check("example.txt")); // => "Yes"
        System.out.println(solution.file_name_check("1example.dll")); // => "No"
        System.out.println(solution.file_name_check("test123.exe")); // => "Yes"
        System.out.println(solution.file_name_check("1234test.txt")); // => "No"
        System.out.println(solution.file_name_check("test..txt")); // => "No"
        System.out.println(solution.file_name_check(".txt")); // => "No"
    }
}

