class Solution {
    public int sum_squares(List<Integer> lst) {
        int sum = 0;

        for (int i = 0; i < lst.size(); i++) {
            int num = lst.get(i);

            if (i % 3 == 0) {
                // Square the number if the index is a multiple of 3
                sum += num * num;
            } else if (i % 4 == 0) {
                // Cube the number if the index is a multiple of 4 and not a multiple of 3
                sum += num * num * num;
            } else {
                // Add the number as it is
                sum += num;
            }
        }

        return sum;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        List<Integer> lst1 = Arrays.asList(1, 2, 3);
        System.out.println(solution.sum_squares(lst1)); // Output: 6

        List<Integer> lst2 = Arrays.asList();
        System.out.println(solution.sum_squares(lst2)); // Output: 0

        List<Integer> lst3 = Arrays.asList(-1, -5, 2, -1, -5);
        System.out.println(solution.sum_squares(lst3)); // Output: -126
    }
}

