class Solution {
    public String words_in_sentence(String sentence) {
        // Split the sentence into words
        String[] words = sentence.split(" ");

        // List to store words with prime lengths
        List<String> primeLengthWords = new ArrayList<>();

        // Check each word's length
        for (String word : words) {
            if (isPrime(word.length())) {
                primeLengthWords.add(word);
            }
        }

        // Join the words with prime lengths into a single string
        return String.join(" ", primeLengthWords);
    }

    // Helper method to check if a number is prime
    private boolean isPrime(int num) {
        if (num <= 1) return false; // 0 and 1 are not prime numbers
        if (num == 2) return true; // 2 is the only even prime number
        if (num % 2 == 0) return false; // Exclude other even numbers

        // Check for factors from 3 to sqrt(num)
        for (int i = 3; i * i <= num; i += 2) {
            if (num % i == 0) return false;
        }

        return true;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.words_in_sentence("This is a test")); // Output: "is"
        System.out.println(solution.words_in_sentence("lets go for swimming")); // Output: "go for"
    }
}

