class Solution {
    public boolean simplify(String x, String n) {
        // Split the fractions into numerator and denominator
        String[] partsX = x.split("/");
        String[] partsN = n.split("/");

        // Convert the parts to integers
        int numX = Integer.parseInt(partsX[0]);
        int denX = Integer.parseInt(partsX[1]);
        int numN = Integer.parseInt(partsN[0]);
        int denN = Integer.parseInt(partsN[1]);

        // Multiply the numerators and denominators
        int resultNum = numX * numN;
        int resultDen = denX * denN;

        // Check if the result is a whole number
        return resultNum % resultDen == 0;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.simplify("1/5", "5/1")); // true
        System.out.println(solution.simplify("1/6", "2/1")); // false
        System.out.println(solution.simplify("7/10", "10/2")); // false
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    public List<Integer> order_by_points(List<Integer> nums) {
        // Create a list of pairs (index, value) to keep track of original indices
        List<Pair<Integer, Integer>> indexedNums = new ArrayList<>();
        for (int i = 0; i < nums.size(); i++) {
            indexedNums.add(new Pair<>(i, nums.get(i)));
        }

        // Sort the list based on the sum of digits and then by original index
        indexedNums.sort((a, b) -> {
            int sumA = sumOfDigits(a.getValue());
            int sumB = sumOfDigits(b.getValue());
            if (sumA != sumB) {
                return Integer.compare(sumA, sumB);
            } else {
                return Integer.compare(a.getKey(), b.getKey());
            }
        });

        // Extract the sorted values
        List<Integer> result = new ArrayList<>();
        for (Pair<Integer, Integer> pair : indexedNums) {
            result.add(pair.getValue());
        }

        return result;
    }

    // Helper method to calculate the sum of digits of a number
    private int sumOfDigits(int num) {
        boolean isNegative = num < 0;
        int sum = 0;
        while (num != 0) {
            int digit = num % 10;
            sum += digit;
            num /= 10;
        }
        return isNegative ? sum * 2 - num : sum; // Adjust for the first digit being negative
    }

    // Simple Pair class to hold index and value
    static class Pair<K, V> {
        private K key;
        private V value;

        public Pair(K key, V value) {
            this.key = key;
            this.value = value;
        }

        public K getKey() {
            return key;
        }

        public V getValue() {
            return value;
        }
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.order_by_points(Arrays.asList(1, 11, -1, -11, -12))); // [-1, -11, 1, -12, 11]
        System.out.println(solution.order_by_points(Arrays.asList())); // []
    }
}

