class Solution {
    public int specialFilter(List<Integer> nums) {
        int count = 0;

        for (int num : nums) {
            if (num > 10 && isFirstAndLastDigitOdd(num)) {
                count++;
            }
        }

        return count;
    }

    private boolean isFirstAndLastDigitOdd(int num) {
        // Convert the number to a positive value to handle negative numbers
        num = Math.abs(num);

        // Extract the last digit
        int lastDigit = num % 10;

        // Extract the first digit
        int firstDigit = num;
        while (firstDigit >= 10) {
            firstDigit /= 10;
        }

        // Check if both first and last digits are odd
        return (firstDigit % 2 != 0) && (lastDigit % 2 != 0);
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        List<Integer> test1 = Arrays.asList(15, -73, 14, -15);
        System.out.println(solution.specialFilter(test1)); // Output: 1

        List<Integer> test2 = Arrays.asList(33, -2, -3, 45, 21, 109);
        System.out.println(solution.specialFilter(test2)); // Output: 2
    }
}

import java.util.*;

class Solution {
    public int get_max_triples(int n) {
        // Step 1: Generate the array a
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = (i + 1) * (i + 1) - (i + 1) + 1;
        }

        // Step 2: Count valid triples
        int count = 0;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                for (int k = j + 1; k < n; k++) {
                    if ((a[i] + a[j] + a[k]) % 3 == 0) {
                        count++;
                    }
                }
            }
        }

        return count;
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        int n = 5;
        System.out.println(sol.get_max_triples(n)); // Output: 1
    }
}

import java.util.*;

class Solution {
    private static final List<String> PLANETS = Arrays.asList(
        "Mercury", "Venus", "Earth", "Mars", "Jupiter", "Saturn", "Uranus", "Neptune"
    );

    public List<String> bf(String planet1, String planet2) {
        int index1 = PLANETS.indexOf(planet1);
        int index2 = PLANETS.indexOf(planet2);

        // If either planet is not found, return an empty list
        if (index1 == -1 || index2 == -1) {
            return Collections.emptyList();
        }

        // Ensure index1 is less than index2
        if (index1 > index2) {
            int temp = index1;
            index1 = index2;
            index2 = temp;
        }

        // Return the sublist of planets between the two indices
        return new ArrayList<>(PLANETS.subList(index1 + 1, index2));
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.bf("Jupiter", "Neptune")); // ["Saturn", "Uranus"]
        System.out.println(solution.bf("Earth", "Mercury"));   // ["Venus"]
        System.out.println(solution.bf("Mercury", "Uranus"));  // ["Venus", "Earth", "Mars", "Jupiter", "Saturn"]
    }
}

