class Solution {
    public List<String> sorted_list_sum(List<String> lst) {
        // Step 1: Filter out strings with odd lengths
        List<String> filteredList = new ArrayList<>();
        for (String s : lst) {
            if (s.length() % 2 == 0) {
                filteredList.add(s);
            }
        }

        // Step 2: Sort the list
        // First, sort alphabetically, then by length
        filteredList.sort((a, b) -> {
            if (a.length() != b.length()) {
                return a.length() - b.length(); // Sort by length
            } else {
                return a.compareTo(b); // Sort alphabetically
            }
        });

        return filteredList;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        List<String> test1 = Arrays.asList("aa", "a", "aaa");
        List<String> test2 = Arrays.asList("ab", "a", "aaa", "cd");

        System.out.println(solution.sorted_list_sum(test1)); // Output: [aa]
        System.out.println(solution.sorted_list_sum(test2)); // Output: [ab, cd]
    }
}

import java.util.*;
