class Solution {
    /**
    A simple program which should return the value of x if n is
    a prime number and should return the value of y otherwise.

    Examples:
    for x_or_y(7, 34, 12) == 34
    for x_or_y(15, 8, 5) == 5
     */
    public int x_or_y(int n, int x, int y) {
        if (isPrime(n)) {
            return x;
        } else {
            return y;
        }
    }

    // Helper method to check if a number is prime
    private boolean isPrime(int n) {
        if (n <= 1) {
            return false; // 0 and 1 are not prime numbers
        }
        if (n == 2) {
            return true; // 2 is the only even prime number
        }
        if (n % 2 == 0) {
            return false; // Exclude all other even numbers
        }
        for (int i = 3; i * i <= n; i += 2) {
            if (n % i == 0) {
                return false; // Found a divisor, so n is not prime
            }
        }
        return true; // No divisors found, n is prime
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.x_or_y(7, 34, 12)); // Output: 34
        System.out.println(solution.x_or_y(15, 8, 5));  // Output: 5
    }
}

