class Solution {
    public List<Integer> compare(List<Integer> game, List<Integer> guess) {
        // Create a list to store the results
        List<Integer> result = new ArrayList<>();

        // Iterate through the lists
        for (int i = 0; i < game.size(); i++) {
            // Calculate the absolute difference between the game score and the guess
            int difference = Math.abs(game.get(i) - guess.get(i));
            // Add the difference to the result list
            result.add(difference);
        }

        // Return the result list
        return result;
    }

    public static void main(String[] args) {
