        // Test cases
        System.out.println(solution.compare(Arrays.asList(1, 2, 3, 4, 5, 1), Arrays.asList(1, 2, 3, 4, 2, -2))); // [0, 0, 0, 0, 3, 3]
        System.out.println(solution.compare(Arrays.asList(0, 5, 0, 0, 0, 4), Arrays.asList(4, 1, 1, 0, 0, -2))); // [4, 4, 1, 0, 0, 6]
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    public String Strongest_Extension(String class_name, List<String> extensions) {
        // Initialize variables to keep track of the strongest extension and its strength
        String strongestExtension = "";
        int maxStrength = Integer.MIN_VALUE;

        // Iterate through each extension
        for (String extension : extensions) {
            // Calculate the strength of the current extension
            int cap = 0, sm = 0;
            for (char c : extension.toCharArray()) {
                if (Character.isUpperCase(c)) {
                    cap++;
                } else if (Character.isLowerCase(c)) {
                    sm++;
                }
            }
            int strength = cap - sm;

            // Update the strongest extension if the current one is stronger
            if (strength > maxStrength) {
                maxStrength = strength;
                strongestExtension = extension;
            }
        }

        // Return the class name and the strongest extension in the required format
        return class_name + "." + strongestExtension;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        List<String> extensions = Arrays.asList("SErviNGSliCes", "Cheese", "StuFfed");
        String result = solution.Strongest_Extension("Slices", extensions);
        System.out.println(result); // Output: Slices.SErviNGSliCes

        extensions = Arrays.asList("AA", "Be", "CC");
        result = solution.Strongest_Extension("my_class", extensions);
        System.out.println(result); // Output: my_class.AA
    }
}

import java.util.*;

class Solution {
    public boolean cycpattern_check(String a, String b) {
        // Concatenate the first string with itself
        String doubledA = a + a;

        // Check if the second string is a substring of the concatenated string
        return doubledA.contains(b);
    }

    public static void main(String[] args) {
        Solution sol = new Solution();

        System.out.println(sol.cycpattern_check("abcd", "abd")); // false
        System.out.println(sol.cycpattern_check("hello", "ell")); // true
