        System.out.println(sol.cycpattern_check("abab", "baa")); // true
        System.out.println(sol.cycpattern_check("efef", "eeff")); // false
        System.out.println(sol.cycpattern_check("himenss", "simen")); // true
    }
}

import java.util.*;

class Solution {
    /**
    Given an integer, return a tuple that has the number of even and odd digits respectively.

     Example:
        even_odd_count(-12) ==> [1, 1]
        even_odd_count(123) ==> [1, 2]
     */
    public List<Integer> even_odd_count(int num) {
        // Convert the number to its absolute value to handle negative numbers
        String numStr = Integer.toString(Math.abs(num));

        // Initialize counters for even and odd digits
        int evenCount = 0;
        int oddCount = 0;

        // Iterate over each character in the string
        for (char c : numStr.toCharArray()) {
            int digit = Character.getNumericValue(c);
            if (digit % 2 == 0) {
                evenCount++;
            } else {
                oddCount++;
            }
        }

        // Return the counts as a list
        return Arrays.asList(evenCount, oddCount);
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.even_odd_count(-12)); // Output: [1, 1]
        System.out.println(solution.even_odd_count(123)); // Output: [1, 2]
    }
}

import java.util.*;

class Solution {
    public String int_to_mini_roman(int number) {
        // Define the Roman numeral mappings
        int[] values = {1000, 900, 500, 400, 100, 90, 50, 40, 10, 9, 5, 4, 1};
        String[] numerals = {"M", "CM", "D", "CD", "C", "XC", "L", "XL", "X", "IX", "V", "IV", "I"};

        StringBuilder roman = new StringBuilder();

        // Iterate through the mappings
        for (int i = 0; i < values.length; i++) {
            while (number >= values[i]) {
                number -= values[i];
                roman.append(numerals[i]);
            }
        }

        // Convert the result to lowercase
        return roman.toString().toLowerCase();
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.int_to_mini_roman(19));  // Output: "xix"
        System.out.println(solution.int_to_mini_roman(152)); // Output: "clii"
        System.out.println(solution.int_to_mini_roman(426)); // Output: "cdxxvi"
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given the lengths of the three sides of a triangle. Return true if the three
    sides form a right-angled triangle, false otherwise.
    A right-angled triangle is a triangle in which one angle is right angle or
    90 degree.
    Example:
    right_angle_triangle(3, 4, 5) == true
    right_angle_triangle(1, 2, 3) == false
     */
    public boolean right_angle_triangle(int a, int b, int c) {
        // First, sort the sides to ensure c is the longest side (hypotenuse)
        int[] sides = {a, b, c};
        Arrays.sort(sides);

        // Apply the Pythagorean theorem: a^2 + b^2 = c^2
        return Math.pow(sides[0], 2) + Math.pow(sides[1], 2) == Math.pow(sides[2], 2);
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.right_angle_triangle(3, 4, 5)); // true
        System.out.println(solution.right_angle_triangle(1, 2, 3)); // false
        System.out.println(solution.right_angle_triangle(5, 12, 13)); // true
        System.out.println(solution.right_angle_triangle(6, 8, 10)); // true
        System.out.println(solution.right_angle_triangle(7, 10, 12)); // false
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    public String find_max(List<String> words) {
        if (words == null || words.isEmpty()) {
            return "";
        }

        // Initialize variables to keep track of the word with the maximum unique characters
        String maxWord = "";
        int maxUniqueCount = 0;

        for (String word : words) {
            // Use a HashSet to count unique characters in the current word
            Set<Character> uniqueChars = new HashSet<>();
            for (char c : word.toCharArray()) {
                uniqueChars.add(c);
            }

            // Get the number of unique characters in the current word
            int uniqueCount = uniqueChars.size();

            // Update the maxWord if the current word has more unique characters
            // or if it has the same number but is lexicographically smaller
            if (uniqueCount > maxUniqueCount || (uniqueCount == maxUniqueCount && word.compareTo(maxWord) < 0)) {
                maxWord = word;
                maxUniqueCount = uniqueCount;
            }
        }

        return maxWord;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        List<String> words1 = Arrays.asList("name", "of", "string");
        List<String> words2 = Arrays.asList("name", "enam", "game");
        List<String> words3 = Arrays.asList("aaaaaaa", "bb", "cc");

        System.out.println(solution.find_max(words1)); // Output: "string"
        System.out.println(solution.find_max(words2)); // Output: "enam"
        System.out.println(solution.find_max(words3)); // Output: "aaaaaaa"
    }
}

import java.util.*;

class Solution {
    public List<Integer> eat(int number, int need, int remaining) {
        // Calculate the total number of carrots eaten after the meals
        int totalEaten = number + Math.min(need, remaining);

        // Calculate the number of carrots left after the meals
        int carrotsLeft = Math.max(remaining - need, 0);

        // Return the results as a list
        return Arrays.asList(totalEaten, carrotsLeft);
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test cases
        System.out.println(solution.eat(5, 6, 10)); // [11, 4]
        System.out.println(solution.eat(4, 8, 9));  // [12, 1]
        System.out.println(solution.eat(1, 10, 10)); // [11, 0]
        System.out.println(solution.eat(2, 11, 5));  // [7, 0]
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    public int do_algebra(List<String> operator, List<Integer> operand) {
        // Initialize the result with the first operand
        int result = operand.get(0);

        // Iterate through the operators and apply them to the operands
        for (int i = 0; i < operator.size(); i++) {
            String op = operator.get(i);
            int nextOperand = operand.get(i + 1);

            switch (op) {
                case "+":
                    result += nextOperand;
                    break;
                case "-":
                    result -= nextOperand;
                    break;
                case "*":
                    result *= nextOperand;
                    break;
                case "/":
                    result /= nextOperand; // Assuming floor division for integer division
                    break;
                case "**":
                    result = (int) Math.pow(result, nextOperand);
                    break;
                default:
                    throw new IllegalArgumentException("Unsupported operator: " + op);
            }
        }

        return result;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        List<String> operator = Arrays.asList("+", "*", "-");
        List<Integer> operand = Arrays.asList(2, 3, 4, 5);

        int result = solution.do_algebra(operator, operand);
        System.out.println("Result: " + result); // Output should be 9
    }
}

import java.util.*;
import java.lang.*;

class Solution {
    public String solve(String s) {
        // Check if the string contains any letters
        boolean containsLetters = false;
        for (char c : s.toCharArray()) {
            if (Character.isLetter(c)) {
                containsLetters = true;
                break;
            }
        }

        // If the string contains no letters, reverse the string
        if (!containsLetters) {
            return new StringBuilder(s).reverse().toString();
        }

        // If the string contains letters, change their case
        StringBuilder result = new StringBuilder();
        for (char c : s.toCharArray()) {
            if (Character.isLetter(c)) {
                if (Character.isLowerCase(c)) {
                    result.append(Character.toUpperCase(c));
                } else {
                    result.append(Character.toLowerCase(c));
                }
            } else {
                result.append(c);
            }
        }

        return result.toString();
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.solve("1234")); // Output: "4321"
        System.out.println(solution.solve("ab"));   // Output: "AB"
        System.out.println(solution.solve("#a@C")); // Output: "#A@c"
    }
}

import java.math.BigInteger;
import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;
import java.util.Optional;

class Solution {
    /**
     * Given a string "text", return its md5 hash equivalent string with length being 32.
     * If "text" is an empty string, return Optional.empty().
     *
     * @param text The input string to convert to MD5 hash.
     * @return An Optional containing the MD5 hash of the input string, or Optional.empty() if the input is an empty string.
     */
    public Optional<String> string_to_md5(String text) throws NoSuchAlgorithmException {
        if (text.isEmpty()) {
            return Optional.empty();
        }

        MessageDigest md = MessageDigest.getInstance("MD5");
        byte[] messageDigest = md.digest(text.getBytes());

        BigInteger no = new BigInteger(1, messageDigest);
        String hashtext = no.toString(16);

        // Add leading zeros to make it 32 characters long
        while (hashtext.length() < 32) {
            hashtext = "0" + hashtext;
        }

        return Optional.of(hashtext);
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        try {
            System.out.println(solution.string_to_md5("Hello world").orElse("No hash generated"));
            System.out.println(solution.string_to_md5("").orElse("No hash generated"));
        } catch (NoSuchAlgorithmException e) {
            e.printStackTrace();
        }
    }
}

import java.util.*;

class Solution {
    public List<Integer> generate_integers(int a, int b) {
        // Ensure a is less than or equal to b
        if (a > b) {
            int temp = a;
            a = b;
            b = temp;
        }

        // List to store even digits
        List<Integer> evenDigits = new ArrayList<>();

        // Set of even digits
        Set<Integer> evenSet = new HashSet<>(Arrays.asList(0, 2, 4, 6, 8));

        // Iterate through the range and add even digits to the list
        for (int i = a; i <= b; i++) {
            if (evenSet.contains(i % 10)) {
                evenDigits.add(i % 10);
            }
        }

        // Remove duplicates and sort the list
        evenDigits = evenDigits.stream().distinct().sorted().collect(Collectors.toList());

        return evenDigits;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.generate_integers(2, 8)); // [2, 4, 6, 8]
        System.out.println(solution.generate_integers(8, 2)); // [2, 4, 6, 8]
        System.out.println(solution.generate_integers(10, 14)); // []
    }
}
